import { Pipe,PipeTransform } from "@angular/core";

@Pipe({
    name:'dateformat'
})
export class DateFormat implements PipeTransform{
    transform(date: Date, format:string) {
        if(typeof date==='string'){
            date=new Date(date)
        }
        
        return date.toLocaleDateString();
    }
}
